insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Жена убийцы', 'У всех нас есть бывший, о котором мы сожалеем. Но бывший муж прокурора Джессики Ярдли – вот это настоящий кошмар. Он – печально известный серийный убийца Эдди Кэл, уже четырнадцать лет сидящий в тюрьме в ожидании казни. Казалось, Джессике удалось справиться с кошмарами прошлого – обрести веру в себя, стать успешным юристом, начать новую счастливую жизнь вместе с дочкой… Казалось ровно до тех пор, пока у Эдди не появился подражатель, копирующий его почерк. Убийца жестоко расправляется с семейными парами, оставляя в живых их детей.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Тайна золотого времени', '""Овидий"" - так называется корабль, на котором читателю вместе с Дэвидом Мирром (главным героем книги) предстоит совершить не одно увлекательное путешествие, окунувшись в насыщенный событиями XVII век.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Лучший из лучших', 'Южная Африка. Далекий край, где только что открыли одно из богатейших месторождений алмазов. Именно сюда приезжает из Англии Зуга Баллантайн, отчаянно смелый человек, готовый на все, лишь бы выбиться из нищеты. С ним – его сестра, красавица Робин, и сыновья – лихой авантюрист Ральф и умный, циничный Джордан. Здесь им всем предстоит пережить жестокие войны и опасные приключения, кровавую семейную вражду и пылкие страсти… Потрясающая сага Уилбура Смита переносит читателя в один из самых интересных исторических периодов – эпоху освоения европейцами необъятного Черного континента.', '0', '294', '7', '7', '21', '1', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Новости со всех концов света', 'Кажется, кровопролитным "индейским войнам" на Западе США никогда не будет конца. Белые и краснокожие, осатанев от взаимной ненависти, безжалостно истребляют друг друга. Правда, индейцам хотя бы хватает милосердия щадить белых детей, которых они увозят с собой и воспитывают в своих племенных традициях… что создает немалые проблемы тем немногим родственникам, которым удается впоследствии чудом разыскать и выкупить юных пленников – таких, как десятилетняя Джоанна Леонбергер.', '0', '333', '8', '6', '21', '4', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Смертельный эликсир', 'Молодой ученый Франсуа Тарпи возвращается в родной городок, чтобы работать над созданием эликсира вечной молодости, рецепт которого готов купить богатый парижский предприниматель Дюлок. Втайне от всех ученый проводит жуткие эксперименты. Волей случая он узнает о загадочных жителях деревни Нуайе. Карлики-отшельники, удивительная моложавость которых породила суеверные слухи. Кажется, ключ к разгадке в руках Франсуа... Но теперь ученого обвиняют в нелегальной операции и сажают в тюрьму. Люди Дюлока переворачивают вверх дном дом Тарпи, чтобы похитить результаты исследований. А тем временем из подвала этого дома на свет выходит то, что способно уничтожить весь городок. Творение ученого, результат неудачного эксперимента...', '0', '601', '7', '6', '21', '4', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Возвращение "Летучего голландца"', 'В районе мыса Доброй Надежды орудуют современные пираты. Их судно появляется из ниоткуда, как призрак, захватывая лайнеры и яхты миллионеров. Атаке предшествует появление «Летучего Голландца». Но ведь это не более чем древняя легенда?.. Фотограф Быков и его друг Алекс Брин в компании с тележурналистами прибывают в район мыса доброй Надежды, чтобы заснять корабль-призрак и разгадать тайну его происхождения. Вот только для пиратов Быков и его команда — как кость в горле. Они начинают охоту на путешественников. И «Летучий Голландец» уже мчится по их следам…', '0', '527', '7', '6', '20', '14', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Путешествия и приключения капитана Гаттераса', 'Бриг "Форвард" отплыл из Ливерпульского порта 6 апреля 1860 года. И никто из восемнадцати членов его экипажа не знал, стоит ли им надеяться на возвращение домой. Ведь они отправились… в никуда. Не зная ни цели плавания, ни даже имени капитана. Лишь углубившись в самое сердце арктических вод, моряки выяснили, что руководит экспедицией знаменитый мореплаватель Джон Гаттерас. И его целью является почти невыполнимая задача - впервые в истории достичь Северного полюса. И ради этого он готов на все…', '0', '606', '7', '6', '21', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Письмо и пуля', 'Новый том Майн Рида в серии "Мастера приключений" состоит из трех разделов. В первый включена авантюрная повесть плаща и шпаги "Письмо и пуля" - история совершенно самостоятельная, но хронологически примыкающая к романам "Без пощады!" и "Белая перчатка". Во второй раздел входит роман "Охотники на медведей" - кругосветная эскапада в духе еще не написанных книг Жюля Верна. Главные герои романа - совсем молодые русские аристократы-путешественники и их верный слуга, отставной унтер-офицер Пушкин. Третий раздел состоит из довольно редких рассказов, многие из которых никогда не публиковались в книжном формате даже на языке оригинала. В одной из этих "потерянных историй" вас ждет новая встреча с неунывающим балагуром-охотником Зебом Стумпом, известным каждому почитателю Майн Рида по лихому вестерну "Всадник без головы".', '0', '329', '7', '6', '21', '1', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Далеко от неба', 'Эта история основана на реальных событиях, начавшихся в далеком 1908 году, когда казачий конвой, сопровождавший годовую добычу золота с Бодайбинских приисков, вынужден был изменить свой привычный маршрут к Лене на неведомую тропу через таинственный Северо-Муйский хребет. Путникам пришлось пережить немало приключений, отбиваясь от бандитов и отыскивая дорогу через неизведанные дебри… Рассказ об утерянном золоте стал местной легендой в небольшом таежном поселке, верить в которую после всех событий, обрушившихся на Россию в ХХ веке, почти перестали. Но череда трагических и таинственных происшествий вновь напомнила о его существовании.', '2', '373', '8', '6', '21', '1', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Комендантский год', 'На оккупированной фашистами территории Орловской области создано так называемое Локотское самоуправление антисоветский регион под управлением бургомистра Каминского. Полная автономия и собственная "освободительная армия" чем не союзники Гитлеру? Накануне наступления Красной Армии СМЕРШ разрабатывает операцию по уничтожению главаря автономии. Майору контрразведки Вадиму Зорину поручено через сестру Каминского убедить бургомистра сдаться. В противном случае предателя ждет смерть. Границу мятежной области оперативная группа прошла беспрепятственно. А дальше начались непредвиденные трудности.', '0', '171', '4', '3', '20', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Пикассо в окружении друзей', 'Пабло Пикассо был вдохновлен француженкой Фернандой Оливье, с которой он познакомился в начале своего «розового» периода в 1904 году. На восемь лет она стала его верной спутницей, музой и моделью, засвидетельствовавшей творческие поиски раннего Пикассо и рождение кубизма. Когда Пикассо наконец добился успеха как художник, он начал терять интерес к Фернанде, которая так и осталась для него воспоминанием о трудных временах. Спустя почти двадцать лет после отношений с Пикассо Фернанда Оливье опубликовала мемуары об их совместной жизни. С 1896 по 1907 год она вела дневник, который лег в основу этих воспоминаний о бурной молодости, ярком романе, Пикассо и его друзьях.', '5', '471', '3', '5', '21', '7', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Волкодав Сталина', 'Павел Судоплатов - легендарный советский разведчик-ликвидатор. Он лично уничтожил главу Организации Украинских националистов Коновальца, который был учителем Бандеры; руководил операцией по ликвидации Троцкого; организовывал и координировал в НКВД-НКГБ разведку и диверсии за линией фронта в годы Великой Отечественной войны; громил бандеровское подполье после нее и участвовал в операции по похищению американских атомных секретов. А после смерти Сталина оказался ""врагом народа"" и 15 лет провел в лагерях.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Анна Ахматова. Я научилась просто, мудро жить...', '""Вы знаете что такое любовь? Настоящая любовь? Любили ли вы так неистово, что готовы были шагнуть в пламя преисподней? Я - да"". С этих слов начинается знаменитая киноповесть, посвященная итальянскому художнику Амедео Модильяни. Так начиналась история мимолетной и трагической любви двух гениев начала века: Анны Ахматовой и Амедео Модильяни.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Анна Керн. Муза А.С. Пушкина', 'Анну Керн все знают как женщину, вдохновившую «солнце русской поэзии» А. С. Пушкина на один из его шедевров. Она была красавицей своей эпохи, вскружившей голову не одному только Пушкину.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Любимец Гитлера: От рядового вермахта до командующего дивизией СС. Сражаясь на Восточном фронте', 'Леон Дегрелль — бельгийский политический деятель ультраправого толка, один из лидеров европейского коллаборационизма. Летом 1941 года стал инициатором создания в составе Вермахта Добровольческого корпуса "Валлония", куда поступил служить рядовым. Летом 1943 года "Валлония" была передана из Вермахта в СС и переименована в Штурмовую бригаду СС, которую в феврале 1944 года возглавил Дегрелль. В январе 1945 года бригада стала 28-й добровольческой дивизии СС "Валлония", а ее командующим назначили Дегрелля. Почти всю войну он провел на Восточном фронте. Осенью 1945 года Леон Дегрелль приговорен заочно бельгийским судом к смертной казни.', '5', '593', '7', '6', '21', '14', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Императрица Елизавета Алексеевна', 'Личность супруги императора Александра I, Елизаветы Алексеевны, известна недостаточно широко: она всегда держалась в тени своей властной свекрови, императрицы Марии Федоровны. В юности, когда она жила при дворе Екатерины II, ее красоту воспевали художники и поэты. При воцарении супруга Елизавета не потеряла присутствия духа и всячески оказывала нравственную поддержку Александру в устройстве политических реформ. Позже, покинутая мужем, она нашла в себе силы заняться благотворительностью и встала во главе первых в России женских общественных организаций. В годы наполеоновских войн она заботилась о раненых, о семьях погибших, но и о пленных; поддерживала бодрость духа и веру в удачный исход компании.', '5', '182', '3', '3', '20', '1', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Жуков. Мифы и правда о маршале Победы', 'Его величают Маршалом Победы и "полководцем, выигравшим Вторую мировую войну". Его имя навсегда вписано в скрижали русской воинской славы, а конный памятник водружен у стен Кремля. Удостоенный высшей чести – принимать Парад Победы, – маршал Жуков провозгласил с трибуны Мавзолея: "Подняв меч против нас, немцы нашли гибель от нашего меча. Отечественная война завершена. Одержана победа, какой еще не знала история!" Именно за это на Жукова в последние годы ополчились историки-ревизионисты во главе с Виктором Суворовым, которые пытаются, очернив Маршала Победы, бросить тень и на главный триумф СССР, и на всю советскую эпоху. Эта книга опровергает все обвинения в адрес Жукова, убедительно доказывая, что Георгий Константинович был лучшим полководцем Красной Армии, а его вклад в Победу невозможно переоценить.', '5', '485', '7', '6', '21', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Бенджамин Франклин. Автобиография', 'Бенджамин Франклин - человек невероятного значения в истории Америки. Он был политиком, дипломатом, писателем, изобретателем и единственный отцом-основателем, кто поставил свою подпись под тремя важнейшими документами эпохи: Декларацией независимости США, Конституции США и Версальским мирным договором 1793 года, завершившим войну за независимость. Его лицо украшает стодолларовую купюру, и он является автором крылатого выражения «Время - деньги», и он знал, о чем говорит.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Черчилль. Время - плохой союзник', 'Бывают ли люди, которым удается все, за что они берутся? Люди, одаренные таким букетом талантов, какого хватило бы на десятерых, и силой воли, способной преодолеть любые преграды?
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Властелины бесконечности: Космонавт о профессии и судьбе', 'Эта книга о космосе как тысячелетней мечте, как путеводной звезде и как испытании. Зачем люди стремятся в космос? На какие вопросы ищут ответы? Как отбирают в космонавты и как их готовят к полету? Что означает формула "здоровье как у космонавта"? Испытывают ли космонавты страх? Что космонавты делают на борту и в открытом космосе? Какими возвращаются? Чему космос способен научить и как можно применять эти знания на Земле? Наконец, в чем суть профессии "космонавт"? На эти и многие другие вопросы отвечает летчик-космонавт России Юрий Батурин. Автор рассказывает и о своем пути в космонавты. Книга иллюстрирована рисунками, схемами и фотографиями, сделанными автором в космосе.', '5', '692', '8', '7', '21', '14', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Госпожа Бовари', 'Эмма Бовари несчастна в браке: она вышла замуж за заурядного человека, но продолжает искать всепоглощающую любовь и мечтает о светской жизни. Встреча с владельцем поместья Родольфом Буланже, известным покорителем сердец, дает ей такую надежду. Она влюблена, готова ради обретения счастья на все, но исход этой страсти непредсказуем... Публикация романа "Госпожа Бовари" принесла Гюставу Флоберу не только скандальную славу, но и статус мастера французского реализма, а характер Эммы Бовари - "тоска несостоявшейся женщины", до сих пор вдохновляет писателей и художников.', '0', '251', '7', '6', '21', '18', 'false');
