insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Сказки дядюшки Римуса', '""Сказки дядюшки Римуса"" – удивительные в своей простоте и мудрости истории, ставшие классикой детской литературы. Эти сказки уже многие годы учат читателей смекалке, доброте и храбрости, смешат и просто рассказывают о главных жизненных истинах. Братец Лис и братец Кролик, братец Черепаха и братец Сарыч – в нашем сборнике все эти герои оживают в иллюстрациях знаменитого художника Вадима Челака.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Билет в никуда', 'Сражаясь за жизнь и право снова увидеть сына, Фрэнк Киттридж был вынужден убивать. Оставшись в одиночестве на марсианской базе, он решается на заключение сделки с корпорацией, пославшей его на смерть. Фрэнк знает, что для них он – "макака", но за возможность вернуться на Землю он готов терпеть и перевоплотиться в надсмотрщика Брэка, чтобы встретить астронавтов НАСА. Но неожиданное открытие ставит под угрозу не только возвращение домой, но и само существование Фрэнка – он не единственный человек на Марсе.', '0', '424', '7', '6', '21', '4', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Безмолвные', 'Сто лет назад на мир, в котором живет Шай, обрушилась таинственная болезнь: чернила распространялись по венам, убивая людей. Поселения вымирали быстрее, чем успевали понять от чего. Тогда могущественными магами — бардами — было принято решение запретить слова, которые несут угрозу. Прошло много лет, но болезнь так и не исчезла. А Шай начала замечать странные вещи вокруг себя. Стоило ей закончить вышивку, как неподалеку она материализовывалась. Испугавшись, что это симптомы болезни, девушка решает рассказать обо всем бардам. Но маги уверены, что она здорова. А на следующий день Шай находит свою мать с кинжалом в груди… В деревне убеждены, что женщина погибла из-за несчастного случая, хотя все указывает на убийство. Не зная, кому доверять, Шай отправляется на поиски правды, но лишь сталкивается с новой ложью, и от нее уже невозможно сбежать…', '0', '371', '7', '6', '20', '7', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Хрустальный мир', 'Пророчество ведьмы сбывается: проклятие набирает силу, и теперь, когда Джун и Блейк стали гораздо ближе друг к другу, Грин-Манор поглотила зловещая тьма. У Джун остается совсем немного времени, чтобы остановить неизбежное и не дать Блейку погибнуть. С помощью особого дара она пытается проникнуть в прошлое, чтобы выяснить, как уничтожить проклятие. Но правда у каждого своя, и чтобы узнать то, что случилось на самом деле, Джун предстоит расширить границы хрустального мира.', '3', '415', '7', '6', '21', '7', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Сердце, что растопит океан', 'Когда-то остров Коа’Коа был райским местом, но всё изменилось в день исчезновения моего дедушки. После этого мир сковала вечная зима, и замерзший океан отрезал нас от духов, что заботливо оберегали наш народ с начала времён. Я родилась намного позже трагических событий, но именно мне выпал шанс отправиться в опасное путешествие и узнать, что же случилось тридцать лет назад. Если я разгадаю эту загадку, то смогу растопить многолетние льды и вернуться домой. Если же потерплю неудачу… это может стоить мне жизни.', '5', '376', '7', '6', '21', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Якса. Царство железных слез', 'Королевство Лендия пало на колени перед дикой ордой кочевников. Предатели становятся победителями, а останки великих воинов гниют в полях. Те же, кому удалось уцелеть, прячутся в лесах как дикие звери. Всеобщей сумятицей пользуются колдуны, призывая старых богов. И опьяненные пролитой кровью, на разоренную землю выходят настоящие чудовища, а демоны с иной стороны овладевают душами людей и целыми городами. Посреди хаоса и кровопролития начинается история о Яксе, обреченном на смерть воине, проклятом с рождения из-за деяний отца и матери. Кажется, что у него нет шансов в этой бесконечной погоне, но судьба снова и снова благоприятствует ему. Только он еще не знает, что удача никогда не дается бесплатно и за цепочкой счастливых случайностей стоит нечто куда страшнее любого преследования.', '0', '539', '8', '6', '21', '4', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Зона Посещения. Забытые богами', 'Зона Посещения, со всеми ее мутантами, артефактами, ловушками и прочими нарушениями нормальных законов природы, для человечества в обычном мире — нечто небывалое, далекое, не грозящее жизни. Но что, если небеса приговорят землю и все вокруг начнет превращаться в сплошную Зону? Вдруг оживут и материализуются прямо здесь, рядом, все худшие виды страха, начиная с убийственных пандемий, реальных монстров, катаклизмов и завершая разрушением основ человеческой цивилизации… Забытый богами мир обречен, конец света неизбежен? Найдется ли какая-нибудь сила, способная противостоять исполнению приговора?', '0', '392', '8', '6', '20', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Дженнифер Морг', 'Его зовут Говард. Боб Говард. Он — программист и время от времени полевой агент в Прачечной, подразделении Секретной службы Ее Величества по борьбе с оккультными угрозами. На этот раз его отправляют на побережье Карибского моря. Говард должен проникнуть на яхту миллиардера, который хочет поднять подлодку, затонувшую в этом районе много лет назад. На ней когда-то пытались установить контакт с миром мертвых, но вместо этого экипаж привлек к себе внимание зловещей подводной расы, которая и прекратила эксперимент самым радикальным способом. Теперь древние вновь обратили внимание на людей, и Говарду нужно предотвратить глобальный катаклизм. Правда, есть одна проблема: когда в напарницах у тебя суккуб, а за каждое действие ты несешь личную финансовую ответственность, спасти мир очень непросто.', '10', '605', '7', '6', '21', '4', 'false');
