insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('История одного профессора', 'Книга погружает читателя в историю о бесконечной войне между небесами и адом. Чем больше читатель будет погружаться в события, тем очевиднее для него будет становиться простая истина — не все ангелы абсолютно чисты, и не все демоны абсолютно темны. Долгая жизнь среди людей изменила и божественные сущности, и адские создания.', '0', '517', '3', '6', '21', '7', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Сверхъестественное. Врезано в плоть', 'Двадцать два года назад Сэм и Дин Винчестеры потеряли мать, которую погубила таинственная злая сила. Когда они выросли, отец рассказал им о демонах, которые таятся во тьме и бродят по проселочным дорогам Америки. А еще он рассказал им о том, как с ними бороться…
