insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Тайна трех государей', 'Величайшая тайна всех времен и народов уходит корнями в глубь веков. К ней прикасались библейские пророки и апостолы во главе с Андреем Первозванным, российские государи Иван Грозный, Петр Первый и Павел, великие писатели и ученые, знаменитые воины и политики. Пока люди бились над ее разгадкой, жернова истории перемололи не один народ и не одну империю. Теперь настало время избранных, которые откроют тайну всему человечеству и круто изменят ход мировой истории. Это случится в России, в Санкт-Петербурге. Здесь и сейчас.', '0', '471', '7', '7', '21', '4', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Ветвь Долгорукого', 'Конец XII века. Необычная судьба выпала юному Олексе — и в Константинополе побывал, и Святую землю видел, многого насмотрелся, но тянет его домой, а там тоже нескучно. Служит он во Владимире сыновьям Юрия Долгорукого. Сначала — храброму князю Михаилу, затем — его брату Всеволоду, на себе испытывая многие последствия междоусобиц и нестроений на Руси. Затем ветер странствий заносит его в Грузию, где обосновался князь Юрий Андреевич, внук Долгорукого, муж-соправитель царицы Тамар. Так перед глазами Олексы проходит вся история рода Долгорукого, а история это долгая, и до конца еще далеко.', '4', '559', '7', '6', '21', '7', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Люди государевы', 'Томский бунт 1648 года оказался в одном ряду со знаменитым Соляным бунтом того же времени в Москве, восстаниями в Великом Устюге и Пскове. А началось всё с извета сына боярского Григория Подреза-Плещеева на воеводу Осипа Щербатого, арестовавшего Григория по челобитным томичей, в коих говорилось, что Подрез «завел у себя пиво и брагу и колмыцкой шар, именуетца табак, и продавал, и держал у себя блудных женок…». В Томск Григорий был сослан за колдовство в 1644 году по грамоте царя Михаила Федоровича, приказавшего «смотреть накрепко… чтоб он никаким воровством не воровал», а если нарушит сие указание, то бить батогами и держать в остроге. Но найдется ли указ для авантюрного характера Подреза…
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Хроники Смуты. Юность царицы', 'Читателю предлагается профессиональная научно-художественная историческая реконструкция событий русской Смуты начала XVII века. Вы побываете в старом замке в Карпатах, познакомитесь с юной польской аристократкой Мариной Мнишек и узнаете о ее встрече с будущим русским царем Дмитрием Самозванцем.', '0', '246', '7', '7', '20', '1', 'false');
