insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Демоническая Пятёрка. Проклятье Властелина', 'Вы никогда не задумывались о том, что наш с вами мир — не единственный во Вселенной? Что где-то там существует волшебный мир Голoмерус, в котором правит только добро и светлая магия. Казалось, лучшего места для обитания быть не может, но всё кардинально меняется. Наложенное злым волшебником проклятье впускает в мир зло. Теперь Голомерусом правит Демоническая Пятёрка — пять самых могущественных тёмных магов. И только один человек сможет разрушить проклятье и вернуть добро в мир магии…', '0', '746', '3', '6', '21', '14', 'false');
