insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Хребты Безумия. Том 2', 'Говард Филлипс Лавкрафт, не опубликовавший при жизни ни одной книги, стал маяком и ориентиром жанра литературы ужасов, кумиром как широких читательских масс, так и рафинированных интеллектуалов. Влияние его признавали такие мастера, как Борхес, и такие кумиры миллионов, как Стивен Кинг, его рассказы неоднократно экранизировались, а само имя писателя стало нарицательным. Франсуа Баранже — французский художник, дизайнер компьютерных игр, концептуалист и футурист. Мечту оформить Лавкрафта он вынашивал много лет — и вот наконец мечта сбылась: сперва с иллюстрациями французского мастера вышел «Зов Ктулху», а теперь вашему вниманию предлагается другая классическая повесть — «Хребты Безумия» (том 2). Итак, Мискатоникский университет из города Аркхема отправляет в Антарктиду исследовательскую экспедицию, оснащенную по последнему слову техники. То, что они обнаружат в древних докембрийских окаменелостях, способно перевернуть всю привычную картину мироздания.', '10', '805', '8', '12', '21', '18', 'false');
