insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('В тихом городке у моря', 'Иван Громов всегда мечтал жить у теплого моря. Ему казалось, что под жарким солнцем его сердце, заледеневшее от невзгод и потерь, оттает. И вот он в южном городке, неспешно живущем от одного курортного сезона до другого. А главное — здесь есть море, бескрайнее, синее. Однажды он придет на берег с маленькой девочкой Асей, дочерью своей квартирной хозяйки. Он расскажет Асе про алые паруса, про Ассоль, которая дождалась своего Грея. Он подарит ей мечту, не подозревая, что, придет время, и эта недолюбленная, никому не нужная девочка тоже сделает ему подарок, самый важный и ценный в его жизни.', '0', '189', '3', '3', '20', '14', 'false');
