insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Гибель Иллы. Ледяная тюрьма. Исследователи бесконечности, или Путешествие в вечность', 'В научно-фантастическом романе "Гибель Иллы", пророческом предчувствии Третьего рейха, описывается цивилизация, существующая в самом далеком прошлом, в момент, когда два города спорят за гегемонию: Нур, огромный, распространяющийся по всем направлениям, и Илла, значительно меньших размеров, но имеющая смертоносную армию и "нулевой элемент", настоящую современную атомную бомбу. Действие романа "Ледяная тюрьма", написанного в жанре морских приключений и полного драматических перипетий, происходит в Тихом океане: Карл Кресслер, бывший компаньон банкира Фрэнсиса Дрейка, будучи человеком бессовестным и жестоким, пойдет на все в своем стремлении завладеть состоянием Дрейка.', '0', '1485', '7', '6', '21', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Галактический консул. Блудные братья: Фантастический роман', 'На веселой и беззаботной планете Эльдорадо ксенолог Константин Кратов по прозвищу Галактический Консул впервые сталкивается с эхайнами - потомками неандертальцев, в незапамятные времена вывезенных с Земли, где им угрожало неминуемое истребление предками современных людей. Создав мощную технологическую цивилизацию, эхайны пытаются втянуть человечество в галактическую войну и отомстить за былое унижение. Спасая взятую в заложницы гениальную певицу Озму, Кратов оказывается в самом сердце враждебного мира. Без поддержки, без надежды, без оружия. Но профессиональный опыт, искренность и убежденный гуманизм становятся его главным и неоспоримым оружием.', '0', '1485', '7', '6', '21', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Смерть Ленро Авельца', 'Противостояние мирового правительства с городом-государством Шанхаем окончено. Чтобы предотвратить ядерную войну, Организация стёрла мегаполис с лица земли, попутно убив два миллиона человек. Теперь враги Организации переходят в контратаку. Протесты охватывают всю планету. Конфликт с национальными государствами, не желающими подчиняться единому центру, переходит в критическую фазу. На фоне смертельной борьбы между ТНК, террористами, националистами и глобалистами, накануне всемирной гражданской войны - Ленро Авельц, некогда влиятельный политик, ныне ушедший в тень, возобновляет свой поход - за верховной властью над Организацией и всем миром. Он уверен, он - единственный, кто ещё способен спасти Землю и окончательно объединить человечество.', '10', '805', '7', '6', '21', '14', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Найти свою вселенную', 'Это просто фантазии. Фантазии на тему любви и о том, каким может быть наш мир через тысячу лет. Книга понравится любителям юмора, философии и драматических любовных историй. Если Вы мыслите без простых шаблонов и аксиом, то эта книга для Вас.', '0', '339', '3', '6', '21', '1', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Новая Атлантида: Повести', 'Утопия - это жанр художественной литературы, описывающий модель идеального общества. Впервые само слова "утопия" употребил английский мыслитель XV века Томас Мор. Книга, которую Вы держите в руках, содержит три величайщих в истории литературы утопии.', '0', '437', '7', '6', '21', '18', 'false');
