insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Ведьмин вяз', 'Тоби Хеннесси — беспечный счастливчик, которому всегда и во всем везет. Но однажды он сталкивается в своем доме с грабителями, которые жестоко избивают его и оставляют умирать. И даже тут удача не изменила Тоби — он выжил и постепенно восстанавливается. Пытаясь оправиться от травм, физических и душевных, Тоби решает пожить у своего дяди, в старом семейном доме, который все называют Домом с плющом. Однако надеждам Тоби на спокойную размеренную жизнь в доме детства не суждено сбыться. В старом вязе обнаруживается пугающая находка, которая переворачивает жизнь и самого Тоби, и всех его близких. Расследование давнего преступления открывает перед Тоби бездну: он понимает, что больше не может верить всему тому, в чем никогда не сомневался, что, возможно, его счастливое прошлое — фикция, мираж, а он сам вовсе не тот милый, славный парень, каким всегда себя считал.', '10', '641', '7', '7', '21', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Из-под ногтей', 'Можно ли называть это романом? Скорее, многослойный коллаж из самых разных художественных миров: здесь переплетаются хоррор-детектив, история маниакальной привязанности, постструктуралистский дневник, ряд эстетических эссе, посты из экстремистского телеграмм-канала, материалы уголовных дел, беседы закадычных друзей, комментарии, графика и сноски.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Увеличительное стекло', 'Доктор Тео Крей поймал одного из самых жестоких серийных убийц в истории, используя революционные научные методы. С тех пор в его жизни произошли серьезные перемены: он больше не может преподавать в университете и теперь работает в секретном подразделении Разведывательного управления. Но новая реальность вовсе не по душе Тео, и он берется за очередное расследование.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Весь мир в кармане', 'Рене Реймонд, известный всему миру под псевдонимом Джеймс Хэдли Чейз, вышел из семьи отставного британского офицера, и отец прочил Рене карьеру ученого. Но в 18 лет будущий писатель оставил учебу и навсегда покинул родительский дом. Испробовал немало профессий, прежде чем стал агентом по распространению книг и основательно изучил книжный бизнес изнутри. Впоследствии Чейз вспоминал: «…Пришлось постучать не менее чем в сто тысяч дверей, и за каждой из них мог встретить любого из персонажей своих будущих романов…» В течение почти полувековой писательской деятельности Чейз создал порядка девяноста романов, которые пользовались неизменным успехом у читателей разных стран, и около пятидесяти из них были экранизированы.', '0', '148', '4', '4', '20', '7', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Одно преступное одиночество', 'Тоскливым вечером женщина в черном пальто расклеивает в центре Москвы объявления «Хочу забыть бывшего». Счастье, что первым их увидел именно он, тот, кто готов спасти ее от отчаяния и одиночества, кто никогда не поверит в то, что она готова убить, и не задаст ни единого вопроса о ее прошлом. Но даже он, способный все простить и догадаться обо всем, не знает, что связывает ее, обычную москвичку в черном пальто, и маленькую европейскую королеву без королевства, жившую и растерявшую всех своих подданных больше ста лет назад…', '0', '298', '7', '6', '21', '7', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Молчание ягнят', 'Мы все безумцы или, может быть, это мир вокруг нас сошел с ума? Доктор Ганнибал Лектер - блестящий психиатр, но мир может считать себя в безопасности только до тех пор, пока он будет находиться за стальной дверью одиночной камеры в тюрьме строгого режима. Доктор Лектер - убийца. Он гурман-людоед. Клэрис Стерлинг - курсант академии ФБР. Она восприимчива к чужой беде, и именно это определяет все ее поступки. Судьба заставляет героев действовать совместно в деле о поимке Буффало Билла - опаснейшего маньяка-убийцы. Сенсационная экранизация романа "Молчание ягнят" получила пять премий "Оскар".', '0', '211', '7', '6', '21', '18', 'false');
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Пассажиры', 'Загадочный хакер взломал системы управления восьми самоуправляемых машин, с недавних пор ставших нормой для всех жителей Великобритании. Теперь он угрожает их пассажирам смертью. Среди его заложников – угасающая телезвезда; нелегальный иммигрант; молодая беременная женщина; муж и жена, едущие раздельно; ветеран военного конфликта; желающий покончить с собой мужчина; жена, сбегающая от своего тирана-мужа.
insert into product(title, description, discount_percent, price, cover_id, format_id, sheets_type_id, manufacturer_id, is_deleted) values('Жена убийцы', 'У всех нас есть бывший, о котором мы сожалеем. Но бывший муж прокурора Джессики Ярдли – вот это настоящий кошмар. Он – печально известный серийный убийца Эдди Кэл, уже четырнадцать лет сидящий в тюрьме в ожидании казни. Казалось, Джессике удалось справиться с кошмарами прошлого – обрести веру в себя, стать успешным юристом, начать новую счастливую жизнь вместе с дочкой… Казалось ровно до тех пор, пока у Эдди не появился подражатель, копирующий его почерк. Убийца жестоко расправляется с семейными парами, оставляя в живых их детей.
